import React from 'react';
import Avatar from '@mui/icons-material/AccountCircle';
import './VideoCard.css';
import { videos } from './videos/videos';

function VideoCard({ image, title, channel, views, timestamp, channelImage }) {
    return (
        <div className='videocard'>
             {videos.map((video) => {
                if (movie.title.length > maxCharHome) {
                  movie.title = movie.title.substring(0, maxCharHome);
                  movie.title =
                    movie.title.substring(
                      0,
                      Math.min(movie.title.length, movie.title.lastIndexOf(' '))
                    ) + '...';
                }})}
            <img className='videocard-img' src={image} alt='' />
            <div className='videocard-info'>
                <Avatar
                    className='videocard-avatar'
                    alt={channel}
                    src={channelImage} />
            </div>
            <div className='videocard-text'>
                <h4>{title}</h4>
                <p>{channel}</p>
                <p>{views} views â€¢ {timestamp} </p>
            </div>
        </div>
    );
}

export default VideoCard;
