import React from 'react';
import Avatar from '@mui/icons-material/AccountCircle';
import './VideoCard.css';
import { videos } from '/Users/HP/Desktop/Coding/YT-Clone/yt-clone/';

function VideoCard() {
    return (
        <div className='videocard'>
             {videos.map((video) => {
                if (video.title.length > Number.MAX_VALUE) {
                  video.title = video.title.substring(0, Number.MAX_VALUE);
                  video.title =
                    video.title.substring(
                      0,
                      Math.min(video.title.length, video.title.lastIndexOf(' '))
                    ) + '...';
                }})}
            <img className='videocard-img' src={videos.image} alt='' />
            <div className='videocard-info'>
                <Avatar
                    className='videocard-avatar'
                    alt={videos.channel}
                    src={videos.channelImage} />
            </div>
            <div className='videocard-text'>
                <h4>{videos.title}</h4>
                <p>{videos.channel}</p>
                <p>{videos.views} views â€¢ {videos.timestamp} </p>
            </div>
        </div>
    );
}

export default VideoCard;
